.treeview .list-group{margin-bottom:0}.treeview .list-group-item{padding:4px 12px;white-space:nowrap;margin:1px 0;color:#444;background-color:#fff;font-weight:400;display:flex;flex-flow:row nowrap;align-items:center}.treeview .list-group-item a{display:inline-block;padding:0;margin:0;flex-grow:1}.treeview .list-group-item .icon{margin:0 5px 0 0}.treeview .list-group-item .expand-icon{cursor:pointer}.treeview .list-group-item.node-selected,.treeview .list-group-item.node-selected:hover,.treeview .list-group-item:not(.node-disabled):hover{background-color:#fee188;color:#444}.treeview .list-group-item.node-selected a,.treeview .list-group-item.node-selected:hover a,.treeview .list-group-item:not(.node-disabled):hover a{text-decoration:none}.treeview .list-group-item .badge{float:unset;margin:0 0 0 5px;background-color:#333;color:#fff}.treeview .list-group-item .badge:nth-last-child(1){background-color:#444;color:#fff}.treeview .list-group-item .badge:nth-last-child(2){background-color:#c2dfff;color:#444}.treeview .list-group-item .badge:nth-last-child(3){background-color:#b46f64;color:#fff}.treeview .search-result:hover{border:none;z-index:0}.treeview span.indent{margin-left:10px;margin-right:10px}.treeview span.icon{width:12px;margin-right:5px}.treeview .node-disabled{color:silver;cursor:not-allowed}/*# sourceMappingURL=projects-tree-view.min.css.map */
